import jsonimport oauth2import timeimport urllib2from pymongo import MongoClientfrom GetRawTweets import GetTweetsdef CrawlTweetsToMongoDB(_since_id, _max_id):    f = file("DowJones.json")    s = json.load(f)  # download json file and extract the key words    since_id = _since_id    max_id = _max_id    for m in s:        keywords = m['keywords']        keywords_id = m['keywords_id']        myClient = MongoClient()  # extablish mongoClient        MoDatabase = myClient.TwitterStock  # extablish database        # extablish collection        MoCollection = MoDatabase.DowJones        # instantiate the Class GetTweets        instance = GetTweets(            since_id, max_id, keywords, keywords_id, MoDatabase, MoCollection)        instance.getTweets()  # call the function